import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';


export default function UpdatePdf() {
  const [pdfs, setPdfs] = useState([]);
  const [currentEditId, setCurrentEditId] = useState(null);
  const [editFormData, setEditFormData] = useState({ Name: '', description: '' });
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    const token = localStorage.getItem("token");
    getData(token);
  }, []);

  const getData = async (token) => {
    try {
      const finalURL = `http://localhost:8000/api/pdf`;
      const res = await axios.get(finalURL, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const pdf = res.data?.pdf || [];
      setPdfs(pdf);
    } catch (error) {
      console.log("Error fetching file data:", error);
      throw error;
    }
  };


  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const onSubmitEditForm = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `http://localhost:8000/api/pdf/update/${currentEditId}`,
        editFormData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      Swal.fire({
        icon: 'success',
        title: 'Success!',
        text: 'Updated Successfully!',
      }).then(() => {
        window.location.assign("/");
      });
    } catch (error) {
      console.log(error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while updating the data. Please try again.',
      });
    }
  };

  return (
    <div>
     
      <div id="uploadPdfContainer"> 
        <h2><b>Add New Task</b></h2>
        <form onSubmit={onSubmitEditForm}>
          <div className="form-group">
            <label htmlFor="pdfName">Task Name:</label>
            <input type="text" id="pdfName" value={name} onChange={handleNameChange} required />
          </div>

          <div className="form-group">
            <label htmlFor="pdfDescription">Description:</label>
            <input type="text" id="pdfDescription" value={description} onChange={handleDescriptionChange} required />
          </div>
          
          <button type="submit" id="uploadButton">Add</button>
        </form>
      </div>
    </div>
  );
}
