import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "./style.css";
import NavBar from '../navBar/NavBar';

const PdfList = () => {
  const [pdfs, setPdfs] = useState([]);
  const [message, setMessage] = useState('');

  const removePdf = async (restaurantId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.log("User not authenticated. Redirecting to login page.");
        return;
      }

      const isConfirmed = await Swal.fire({
        title: 'Are you sure?',
        text: 'You want to delete this Restaurant?',
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: 'Yes, delete it!'
      });

      if (isConfirmed.isConfirmed) {
        const finalURL = `${config.apiUrl}/api/restaurant/${restaurantId}`;
        await axios.delete(finalURL, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        Swal.fire(
          'Deleted!',
          'The Restaurant has been deleted.',
          'success'
        ).then(() => {
          window.location.reload(false);
        });
      }
    } catch (error) {
      console.log("Error deleting Restaurant:", error);
      Swal.fire(
        'Error!',
        'Failed to delete the Restaurant.',
        'error'
      );
    }
  };

  useEffect(() => {
    const fetchPdfs = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/pdf');
        setPdfs(response.data.pdf);
      } catch (error) {
        setMessage('Failed to fetch PDFs');
      }
    };

    fetchPdfs();
  }, []);

  return (
    <div>
      <NavBar/>
      <div id="pdfListContainer"> 
        <h2><b><center>PDF List</center></b></h2>
        {message && <p id="errorMessage">{message}</p>} 
        <ul id="pdfList">
          {pdfs.map((pdf, index) => (
            <li key={pdf._id}>
              <a href={`http://localhost:8000/api/pdf/${pdf._id}`} target="_blank" rel="noopener noreferrer">
                {index + 1}. {pdf.Name} <button>Delete</button>
              </a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default PdfList;
