import React, { useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';
import './style.css';
import NavBar from '../navBar/NavBar';

const UploadPdf = () => {
  const [name, setName] = useState('');
  const [file, setFile] = useState(null);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('Name', name);
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:8000/api/pdf/create', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      Swal.fire({
        icon: 'success',
        title: 'Success!',
        text: response.data.message,
      }).then(() => {
        window.location.assign("/list");
      });

    } catch (error) {
      Swal.fire({
        icon: 'error',
        title: 'Oops...',
        text: 'Failed to upload PDF',
      });
    }
  };

  return (
    <div id="root">
      <NavBar />
      <div id="uploadPdfContainer"> 
        <div className="upload-form">
          <h2>Upload PDF</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="pdfName">Name:</label>
              <input type="text" id="pdfName" value={name} onChange={handleNameChange} required className="form-control"/>
            </div>
            <div className="form-group">
              <label htmlFor="pdfFile">PDF:</label>
              <input type="file" id="pdfFile" onChange={handleFileChange} required className="form-control"/>
            </div>
            <button type="submit" id="uploadButton" className="btn btn-primary">Upload</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default UploadPdf;
